INSERT INTO `users` VALUES ('1', 'test', 'asdf', '26', '0', '2017-03-26', '{\"choose\":[\"49\",\"15\",\"54\",\"40\",\"84\",\"21\",\"19\",\"16\",\"5\",\"98\",\"88\",\"70\",\"18\",\"61\",\"69\",\"52\",\"62\",\"99\",\"24\",\"30\",\"27\",\"55\",\"56\",\"28\",\"2\",\"78\",\"77\",\"7\",\"22\",\"82\",\"11\",\"68\",\"12\",\"66\",\"13\",\"64\",\"44\",\"90\",\"1\",\"71\",\"42\",\"33\",\"85\",\"25\",\"59\",\"26\",\"37\",\"92\",\"48\",\"89\",\"87\",\"91\",\"51\",\"36\",\"81\",\"39\",\"79\",\"29\",\"17\",\"83\",\"57\",\"32\",\"53\",\"38\",\"58\",\"60\",\"23\",\"45\",\"73\",\"50\",\"8\",\"10\",\"46\",\"80\",\"72\",\"14\",\"34\",\"47\",\"65\",\"75\"],\"fillblank\":[\"622\",\"319\",\"126\",\"73\",\"622\",\"156\",\"53\",\"72\",\"168\",\"69\",\"262\",\"266\",\"44\",\"253\",\"224\",\"59\",\"276\",\"169\",\"75\",\"287\",\"232\",\"55\",\"206\",\"222\",\"74\",\"20\",\"243\",\"76\",\"333\",\"240\",\"58\",\"281\",\"116\",\"64\",\"638\",\"131\",\"50\",\"679\",\"269\",\"48\",\"146\",\"133\",\"70\",\"267\",\"182\",\"60\",\"630\",\"135\",\"46\",\"120\",\"160\",\"57\",\"396\",\"193\",\"62\",\"655\",\"283\",\"66\",\"391\",\"230\",\"51\",\"376\",\"174\",\"49\",\"43\",\"275\",\"65\",\"365\",\"227\",\"52\",\"659\",\"215\",\"45\",\"307\",\"61\",\"123\",\"297\",\"326\",\"205\",\"68\",\"392\",\"195\",\"54\",\"631\",\"220\",\"47\",\"636\",\"63\",\"35\",\"136\",\"77\",\"339\",\"285\",\"71\",\"154\"]}', '3', 'http://tva4.sinaimg.cn/crop.17.0.467.467.180/dca70594jw8f76cw6mhayj20go0nd7at.jpg');
